import React, { useState, useEffect } from 'react';

function FileManagement() {
  const [file, setFile] = useState(null);
  const [fileList, setFileList] = useState([]);
  const [accessToken] = useState(localStorage.getItem("token"));

  useEffect(() => {
    if (accessToken) {
      loadFileList();
    } else {
      // Redirect to the login page or display an error message
      window.location.href = 'login.html';
    }
  }, [accessToken]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const uploadFile = () => {
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      fetch('http://localhost:8082/api/upload', {
        method: 'POST',
        headers: {
          'access-token': 'Bearer ' + accessToken,
        },
        body: formData,
      })
        .then((response) => response.text())
        .then((data) => {
          alert(data);
          loadFileList();
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    } else {
      alert('Please select a file to upload.');
    }
  };

  const loadFileList = () => {
    fetch('http://localhost:8082/api/files', {
      method: 'GET',
      headers: {
        'access-token': 'Bearer ' + accessToken,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setFileList(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const updateFile = (fileName) => {
    fetch('http://localhost:8082/api/update/?fileName=' + fileName, {
      method: 'PUT',
    })
      .then((response) => response.text())
      .then((data) => {
        alert(data);
        loadFileList();
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const deleteFile = (fileName) => {
    fetch('http://localhost:8082/api/delete/?fileName=' + fileName, {
      method: 'DELETE',
    })
      .then((response) => response.text())
      .then((data) => {
        alert(data);
        loadFileList();
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="container">
      <h2>File Upload</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={uploadFile}>Upload</button>
      <h2>File List</h2>
      <table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {fileList.map((fileObject) => (
            <tr key={fileObject.fileName}>
              <td><a href="https://d2sm8dpuxev7td.cloudfront.net/${fileObject.fileUrl}">{fileObject.fileName}</a></td>
              <td >
                <button onClick={() => updateFile(fileObject.fileName)}>Update</button>
                <button id="delete" onClick={() => deleteFile(fileObject.fileName)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FileManagement;
